package com.xinyue.a30seconds.utils.pay.payimport android.text.TextUtilsclass ALiPayResult constructor(rawResult: Map<String?, String?>?) {    companion object {        const val PAY_OK_STATUS = "9000" // 支付成功        const val PAY_WAIT_CONFIRM_STATUS = "8000" // 交易待确认        const val PAY_FAILED_STATUS = "4000" // 交易失败        const val PAY_CANCLE_STATUS = "6001" // 交易取消        const val PAY_NET_ERR_STATUS = "6002" // 网络出错        const val PAY_UNKNOWN_ERR_STATUS = "6004" // 结果未知    }    init {        rawResult?.let {            for (key in it.keys) {                when {                    TextUtils.equals(key, "resultStatus") -> {                        resultStatus = it[key]                    }                    TextUtils.equals(key, "result") -> {                        result = it[key]                    }                    TextUtils.equals(key, "memo") -> {                        memo = it[key]                    }                }            }        }    }    /**     * @return the resultStatus     */    var resultStatus: String? = null    /**     * @return the result     */    var result: String? = null    /**     * @return the memo     */    var memo: String? = null    override fun toString(): String {        return ("resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}")    }}